export default function(Component) {
  var page = (function(a,b){b.internal=a;b.contentType="markdown";b.slug="test-one";b.content=a;b.layout="default";b.title="test one";b.markdownHeadings=[{text:"This is test one",slug:"this-is-test-one",level:1}];b.createdAt=new Date(1570981631417);b.updatedAt=new Date(1570981656977);b.type="post";b.permalink="\u002Fposts\u002Ftest-one.html";b.assets={};b.attributes=b;b.prevPost=a;b.nextPost={title:"test two",permalink:"\u002Fposts\u002Ftest-two.html"};return b}(void 0,{}))
