import { siteConfig, themeConfig, locales } from 'saber/config'

export default ({ Vue }) => {
  const store = Vue.observable({ siteConfig, themeConfig, locales })

  const getMatchedConfig = (localePath, key) => {
    const matchedLocale = store.locales[localePath]
    return Object.assign({}, store[key], matchedLocale && matchedLocale[key])
  }

  Vue.mixin({
    computed: {
      $localePath() {
        const allLocalePaths = Object.keys(store.locales)
        let localePath = '/'
        for (const path of allLocalePaths) {
          if (path !== '/') {
            if (this.$route.path === path || this.$route.path.indexOf(`${path}/`) === 0){
              localePath = path
            }
          }
        }
        return localePath
      },
      $locales() {
        return store.locales
      },
      $siteConfig() {
        return getMatchedConfig(this.$localePath, 'siteConfig')
      },
      $themeConfig() {
        return getMatchedConfig(this.$localePath, 'themeConfig')
      }
    }
  })

  if (module.hot) {
    module.hot.accept('saber/config', () => {
      const config = require('saber/config')
      store.siteConfig = config.siteConfig
      store.themeConfig = config.themeConfig
      store.locales = config.locales
    })
  }
}

export default ({ router }) => {
  if (process.browser) {
    function normalizeTransition(transition, to, from) {
      if (typeof transition === 'function') {
        transition = transition(to, from)
      } else if (typeof transition === 'string') {
        transition = { name: transition }
      }
      return transition
    }

    async function getTransition(route, args) {
      if (
        !route ||
        !route.matched.length ||
        !route.matched[0].components.default
      ) {
        return
      }
      // The default router component
      let RouteComponent = route.matched[0].components.default
      // Resolve async component
      if (typeof RouteComponent === 'function') {
        RouteComponent = await RouteComponent()
      }
      // ES compat
      RouteComponent = RouteComponent.default || RouteComponent

      const routeTransition = normalizeTransition(
        RouteComponent.transition,
        ...args
      )

      let layoutTransition
      if (RouteComponent.layout) {
        const { layouts } = router.app.$options
        const LayoutComponent =
          layouts[RouteComponent.layout] || layouts.default
        if (LayoutComponent) {
          layoutTransition = normalizeTransition(
            LayoutComponent.transition,
            ...args
          )
        }
      }

      return Object.assign(
        {
          name: 'page',
          mode: 'out-in'
        },
        routeTransition || layoutTransition
      )
    }

    router.beforeEach(async (to, from, next) => {
      const [toTransition, fromTransition] = await Promise.all([
        getTransition(to, [to, from]),
        getTransition(from, [to, from])
      ])

      if (fromTransition) {
        for (const key of Object.keys(fromTransition)) {
          // prefer `leave` transitions of 'from' route
          if (/leave/i.test(key)) {
            toTransition[key] = fromTransition[key]
          }
        }
      }

      router.app.setTransition(toTransition)

      next()
    })
  }
}

import createApp from './create-app'

export default async context => {
  const { app, router } = createApp(context)
  router.push(context.url)

  await new Promise((resolve, reject) => {
    router.onReady(resolve, reject)
  })

  if (context.res && router.currentRoute.name === 404) {
    context.res.statusCode = 404
  }

  context.metaInfo = app.$meta()

  return app
}

import listToStyles from './listToStyles'

export default function addStylesServer (parentId, list, isProduction, context) {
  if (!context && typeof __VUE_SSR_CONTEXT__ !== 'undefined') {
    context = __VUE_SSR_CONTEXT__
  }
  if (context) {
    if (!context.hasOwnProperty('styles')) {
      Object.defineProperty(context, 'styles', {
        enumerable: true,
        get: function() {
          return renderStyles(context._styles)
        }
      })
      // expose renderStyles for vue-server-renderer (vuejs/#6353)
      context._renderStyles = renderStyles
    }

    var styles = context._styles || (context._styles = {})
    list = listToStyles(parentId, list)
    if (isProduction) {
      addStyleProd(styles, list)
    } else {
      addStyleDev(styles, list)
    }
  }
}

// In production, render as few style tags as possible.
// (mostly because IE9 has a limit on number of style tags)
function addStyleProd (styles, list) {
  for (var i = 0; i < list.length; i++) {
    var parts = list[i].parts
    for (var j = 0; j < parts.length; j++) {
      var part = parts[j]
      // group style tags by media types.
      var id = part.media || 'default'
      var style = styles[id]
      if (style) {
        if (style.ids.indexOf(part.id) < 0) {
          style.ids.push(part.id)
          style.css += '\n' + part.css
        }
      } else {
        styles[id] = {
          ids: [part.id],
          css: part.css,
          media: part.media
        }
      }
    }
  }
}

// In dev we use individual style tag for each module for hot-reload
// and source maps.
function addStyleDev (styles, list) {
  for (var i = 0; i < list.length; i++) {
    var parts = list[i].parts
    for (var j = 0; j < parts.length; j++) {
      var part = parts[j]
      styles[part.id] = {
        ids: [part.id],
        css: part.css,
        media: part.media
      }
    }
  }
}

function renderStyles (styles) {
  var css = ''
  for (var key in styles) {
    var style = styles[key]
    css += '<style data-vue-ssr-id="' + style.ids.join(' ') + '"' +
        (style.media ? ( ' media="' + style.media + '"' ) : '') + '>' +
        style.css + '</style>'
  }
  return css
}

/**
 * Translates the list format produced by css-loader into something
 * easier to manipulate.
 */
export default function listToStyles (parentId, list) {
  var styles = []
  var newStyles = {}
  for (var i = 0; i < list.length; i++) {
    var item = list[i]
    var id = item[0]
    var css = item[1]
    var media = item[2]
    var sourceMap = item[3]
    var part = {
      id: parentId + ':' + i,
      css: css,
      media: media,
      sourceMap: sourceMap
    }
    if (!newStyles[id]) {
      styles.push(newStyles[id] = { id: id, parts: [part] })
    } else {
      newStyles[id].parts.push(part)
    }
  }
  return styles
}
